sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var gettingEmailAjax = new GlideAjax("GetEmailAddress");
	gettingEmailAjax.addParam("sysparm_name","getEmailForUserID");
	gettingEmailAjax.addParam("sysparm_user_id",newValue);
	gettingEmailAjax.getXML(setEmailField);
}

function setEmailField(response){
	var emailValue = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue("u_requested_for_email",emailValue);
}
